mapper.insert:
insert into board(
board_title, board_content, created_by, board_image
) values (
#{board_title}, #{board_content}, #{created_by}, #{board_image}
)

mapper.update:
update board
set board_title = #{board_title},
board_content = #{board_content},
board_image = #{board_image}
where board_id = #{board_id}
