mapper.select:
select user_id, email, nickname, password, bio, profile_image, teamlist_id
from users
where email=#{email} and password=#{password}

mapper.update:
UPDATE users
SET nickname = #{nickname} , bio = #{bio}, profile_image = #{profile_image}
WHERE user_id = #{user_id}

mapper.select:
select token
from users
where email = #{email}

mapper.update:
update users
set token = #{token}
where email = #{email}

update users
set token = #{token, jdbcType=VARCHAR}
where email = #{email}
